$(document).ready(function() {
    function destroyCredentials(callback) {
        storage.removeAll(), $.ajaxSetup({
            headers: {}
        }), callback("storage cleaned!")
    }

    function loadVitrine() {
        var getvitrine = $.ajax({
            url: "/api/getvitrine",
            type: "GET",
            beforeSend: function(request) {
                request.setRequestHeader("Authorization", storage.get("token"))
            },
            data: {}
        });
        getvitrine.done(function(data) {
            console.log(data), div_vitrine.text(""), div_vitrine.append(data)
        }), getvitrine.fail(function(xhr, textStatus) {
            console.log(xhr), console.log(textStatus)
        })
    }
    var storage = Storages.localStorage;
    $("#panelbody");
    $("#btnLogout").click(function() {
        $("#btnLogoutLabel").text("Saindo..."), destroyCredentials(function(result) {
            window.location.href = "/entrar"
        })
    }), $("#btnAccount").click(function() {
        window.location.href = "/conta?token=" + storage.get("token")
    });
    var div_produtos = $("#div_produtos");
    storage.isSet("token") && storage.isSet("user") ? ($(".firstname").text(storage.get("user").firstname), $('.nav-tabs a[href="#tab_produtos"]').tab("show"), function() {
        var produtos = $.ajax({
            url: "/api/getuserprodutos",
            type: "GET",
            beforeSend: function(request) {
                request.setRequestHeader("Authorization", storage.get("token"))
            },
            data: {}
        });
        produtos.done(function(data) {
            div_produtos.text(""), div_produtos.append(data)
        }), produtos.fail(function(xhr, textStatus) {
            console.log(xhr), console.log(textStatus)
        })
    }()) : window.location.href = "/entrar", div_produtos.on("click", ".btnOpenProduto", function() {
        var url = "/produto/" + $(this).attr("href") + "/?token=" + storage.get("token");
        console.log(url), window.location.href = url
    });
    var div_vitrine = $("#div_vitrine");
    $('.nav-tabs a[href="#tab_vitrine"]').click(function() {
        loadVitrine()
    })
});