$(document).ready(function() {
    var storage = Storages.localStorage,
        user = storage.get("user");
    $(".firstname").text(user.firstname), $(".btnOpenConteudo").click(function() {
        var este = $(this),
            modulo = este.attr("modulo"),
            conteudo = este.attr("conteudo");
        console.log(modulo), console.log(conteudo);
        var url = "/produto/bem_mais_mulher/modulo/" + modulo + "/conteudo/" + conteudo + "/?token=" + storage.get("token");
        console.log(url), window.location.href = url
    })
});